import { useContext, useEffect,useState } from "react";
import { Store } from "../Store";
import TaskCard from "./TaskCard";
const Tasks = () => {
    let store=useContext(Store);
    const [tasks, setTasks] = useState([]);
    useEffect(()=>{
        let usertasks=store.currUser.tasks;
        console.log(usertasks);
        let temp=usertasks.map((task,index)=>{
            return <TaskCard content={task.content} key={index} id={task.id}/>
        })
        setTasks(temp);
        console.log(tasks);
    },[store.currUser]);
    
    function signUp(){
        setDup("");
        let newUser={
            tasks:[],
            ongoing:[],
            done:[],
            username:signUsername.current.value,
            password:signPass.current.value,
            uniId:0
        };
        fetch("http://localhost:5000/addUser",{
            method: "POST",
            headers:{
                "content-type": "application/json",
            },
            body:JSON.stringify(newUser)
        }).then((response)=>{
            if (!response.ok) {
                throw new Error('Network response was not ok');
              }
            else{
                
                return response.json();
            }
        }).then((data)=>{
            if(data===null){
                setDup("h")
            }
            console.log(data,"hi");
            store.updateCurrUser(newUser);
            localStorage.setItem("currUser",JSON.stringify(newUser));
        })
    }

    return ( 
    <div>
        {tasks}
    </div> 
    );
}
 
export default Tasks;
