import React, { useEffect } from 'react';


const DoneCard = (props) => {
    useEffect(()=>{
        console.log(props.time,props.doneTime);
        console.log(calculateTimeTaken(props.time,props.doneTime));
    })
    function calculateTimeTaken(startTime, endTime) {
        // Split start and end time strings into hours and minutes
        let [startHour, startMinute] = startTime.split(':').map(Number);
        let [endHour, endMinute] = endTime.split(':').map(Number);
        console.log(startMinute,e);
        // Convert PM hours to 24-hour format
        if (startTime.includes('PM')) {
            startHour = (startHour % 12) + 12;
        }
        if (endTime.includes('PM')) {
            endHour = (endHour % 12) + 12;
        }
        console.log(endHour,startHour);
        // Calculate time difference in minutes
        let totalStartMinutes = startHour * 60 + startMinute;
        let totalEndMinutes = endHour * 60 + endMinute;
        let timeDifference = totalEndMinutes - totalStartMinutes;
        // Ensure time difference is positive (if end time is before start time, add 24 hours)
        if (timeDifference < 0) {
            timeDifference += 24 * 60;
        }
    
        return timeDifference;
    }
  return (
    <div className="donecard-wrapper">
      <div style={{ backgroundColor: props.color }} className="donecard">
        <h2>{props.content}</h2>
        <p>taken time:{calculateTimeTaken(props.time,props.doneTime)}</p>
      </div>
    </div>
  );
}

export default DoneCard;
