import { useEffect, useRef, useState, useContext } from 'react';
import { Store } from '../Store';

const Signup = () => {
  useEffect(() => {
    localStorage.removeItem('currUser');
  }, []);

  const store = useContext(Store);
  const [dup, setDup] = useState('');
  const [wrong, setWrong] = useState('');
  const logUsername = useRef('');
  const logPass = useRef('');
  const signUsername = useRef('');
  const signPass = useRef('');

  const signUp = async () => {
    setDup('');
    const newUser = {
      tasks: [],
      ongoing: [],
      done: [],
      username: signUsername.current.value,
      password: signPass.current.value,
      uniId: 0,
    };

    try {
      const response = await fetch('http://localhost:5000/addUser', {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
        },
        body: JSON.stringify(newUser),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();

      if (data === null) {
        setDup('h');
      } else {
        console.log(data, 'hi');
        store.updateCurrUser(newUser);
        localStorage.setItem('currUser', JSON.stringify(newUser));
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  const logIn = async () => {
    setWrong('');
    try {
      const response = await fetch(
        `http://localhost:5000/login/${logUsername.current.value}/${logPass.current.value}`
      );

      const data = await response.json();

      if (data !== null) {
        localStorage.setItem('currUser', JSON.stringify(data));
        store.updateCurrUser(data);
      } else {
        setWrong('h');
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  return (
    <div>
      {dup !== '' && <h1 style={{ textAlign: 'center' }}>Username Already Exists</h1>}
      {wrong !== '' && <h1 style={{ textAlign: 'center' }}>Wrong Username Or Password</h1>}
      <div className="main">
        <input type="checkbox" id="chk" aria-hidden="true" />
        <div className="login">
          <div className="form">
            <label htmlFor="chk" aria-hidden="true">
              Log in
            </label>
            <input
              ref={logUsername}
              className="input"
              type="text"
              name="txt"
              placeholder="Username"
              required=""
            />
            <input
              ref={logPass}
              className="input"
              type="password"
              name="pswd"
              placeholder="Password"
              required=""
            />
            <button onClick={logIn}>Log in</button>
          </div>
        </div>
        <div className="register">
          <div className="form">
            <label htmlFor="chk" aria-hidden="true">
              Register
            </label>
            <input
              ref={signUsername}
              className="input"
              type="text"
              name="txt"
              placeholder="Username"
              required=""
            />
            <input
              ref={signPass}
              className="input"
              type="password"
              name="pswd"
              placeholder="Password"
              required=""
            />
            <button onClick={signUp}>Register</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
