import { useContext, useRef, useState } from "react";
import { Store } from "../Store";
const Ai = () => {


    let selectFromRef=useRef(null);
    let selectToRef=useRef(null);
    let store=useContext(Store);
    let timeFromRef=useRef(null);
    let timeToRef=useRef(null);
    let [schedule,setSchedule]=useState([])
    async function AiCall(){

        let query=getText();
        console.log(query);
        const url = 'https://chatgpt-openai.p.rapidapi.com/chat-completion';
const options = {
	method: 'POST',
	headers: {
		'content-type': 'application/json',
		'X-RapidAPI-Key': '30bbe95bb8mshd18d66efbdf604ep1ab26ajsndc5b0723adc1',
		'X-RapidAPI-Host': 'chatgpt-openai.p.rapidapi.com'
	},
	body: {
		messages: [
			{
				role: 'user',
				content: 'I want a day plan to make go to school and workout and go to college and do assignments and i will start from 8AM to 11AM make your response in the form 1. task 2. task etc'
			}
		],
		temperature: 1
	}
};

try {
	const response = await fetch(url, options);
	const result = await response.j();
	console.log(result);
} catch (error) {
	console.error(error);
}
    // let steps=splitStringToList(result.result);
    // let temp=steps.map(step=>{
    //     return <h4>{step}</h4>
    // })
    // setSchedule(temp);



	
    
    }
    function splitStringToList(str) {
        const splitArray = str.split(/\d+\.\s/).filter(Boolean);
        return splitArray;
    }
    function getText(){
        let str="I want a day plan to make "
        let temp=store.currUser.tasks;
        for(let i=0; i<temp.length-1; i++){
            str+=temp[i].content+" and "
        }
        str+=temp[temp.length-1].content;
        str+=" and i will start from "+ timeFromRef.current.value+selectFromRef.current.value+" to "+timeToRef.current.value+selectToRef.current.value;
        str+=" make your response in the form 1. task 2. task etc"
        return str
    }
    return ( 
        <div>
            <div className="settings" style={{textAlign:'center'}}>
                <span>from</span>
                <input ref={timeFromRef} type="text" />
                <select id="selectOption" ref={selectFromRef}>
                    <option value="AM">PM</option>
                    <option value="PM">AM</option>
                </select>
                <span>to</span>
                <input ref={timeToRef} type="text" />
                <select id="selectOption" ref={selectToRef}>
                    <option value="AM">PM</option>
                    <option value="PM">AM</option>
                </select>
                <button onClick={AiCall}>Create schedule</button>
            </div>
            
            <div>
                {schedule}
            </div>
        </div>
     );
}
 
export default Ai;
