import Signup from './Components/Signup';
import './App.css';
import { Store } from './Store';
import { useContext, useEffect, useState } from 'react';

function App() {
  const store = useContext(Store);
  const [logged, setLogged] = useState(false);

  const checkUserLoginStatus = () => {
    const storedUser = localStorage.getItem('currUser');
    if (storedUser) {
      store.updateCurrUser(JSON.parse(storedUser));
      setLogged(true);
    } else {
      setLogged(false);
    }
  };

  useEffect(() => {
    checkUserLoginStatus();

    window.addEventListener('storage', checkUserLoginStatus);

    return () => {
      window.removeEventListener('storage', checkUserLoginStatus);
    };
  }, []); // Empty dependency array ensures this runs after the initial render

  return (
    <div className="container">
      {!logged && <Signup />}
      {logged && (
        <div>
          <button
            onClick={() => {
              localStorage.removeItem('currUser');
              setLogged(false);
              store.updateCurrUser(null);
            }}
          >
            Logout
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
