import React, { useEffect } from 'react';


const DoneCard = (props) => {
    useEffect(()=>{
        console.log(props.time,props.doneTime);
        console.log(calculateTimeTaken(props.time,props.doneTime));
    })
    function calculateTimeTaken(startTime, endTime) {
        // Convert start and end time strings to Date objects
        let start = new Date("2000-01-01T" + startTime);
        let end = new Date("2000-01-01T" + endTime);
    
        // Calculate time difference in milliseconds
        let timeDifference = end - start;
    
        // Convert milliseconds to minutes
        let timeTakenInMinutes = timeDifference / (1000 * 60);
        log
        return timeTakenInMinutes;
    }
  return (
    <div className="donecard-wrapper">
      <div style={{ backgroundColor: props.color }} className="donecard">
        <h2>{props.content}</h2>
        <p>taken time:{calculateTimeTaken(props.time,props.doneTime)}</p>
      </div>
    </div>
  );
}

export default DoneCard;
