import { useContext, useEffect,useRef,useState } from "react";
import { Store } from "../Store";
import TaskCard from "./TaskCard";
const Tasks = () => {
    let taskRef=useRef("")
    let store=useContext(Store);
    const [tasks, setTasks] = useState([]);
    useEffect(()=>{
        let usertasks=store.currUser.tasks;
        console.log(usertasks);
        let temp=usertasks.map((task,index)=>{
            return <TaskCard content={task.content} key={index} id={task.id}/>
        })
        setTasks(temp);
        console.log(tasks);
    },[store.currUser]);
    
    function addTask(){
        let newObj=store.currUser;
        let newTask={
            content:taskRef.current.value,
            id:newObj.uniId+1
        }
        newObj.tasks.push(newTask);
        fetch("http://localhost:5000/addUser",{
            method: "POST",
            headers:{
                "content-type": "application/json",
            },
            body:JSON.stringify()
        }).then((response)=>{
            if (!response.ok) {
                throw new Error('Network response was not ok');
              }
            else{
                
                return response.json();
            }
        }).then((data)=>{
            
        })
    }

    return ( 
    <div>
        <input ref={taskRef} type="text" />
        <button onClick={addTask}>Add</button>
        {tasks}
    </div> 
    );
}
 
export default Tasks;
