import { useContext, useEffect,useRef,useState } from "react";
import { Store } from "../Store";
import TaskCard from "./TaskCard";
const Tasks = () => {
    let taskRef=useRef("")
    let store=useContext(Store);
    const [tasks, setTasks] = useState([]);
    let [curr,setCurr]=useState(store.currUser);
    const shinyColors = [
        "#ff0000", // Red
        "#00ff00", // Green
        "#0000ff", // Blue
        "#ffff00", // Yellow
        "#ff00ff", // Magenta
        "#00ffff", // Cyan
        "#ff7f00", // Orange
        "#7fff00", // Chartreuse
        "#ff1493", // Deep Pink
        "#9400d3"  // Dark Violet
      ];
      function getRandomNumber() {
        const randomNumber = Math.random();
        const scaledNumber = Math.floor(randomNumber * 10);
        return scaledNumber;
      }
    useEffect(()=>{
        console.log("jnhvgt");
        let usertasks=store.currUser.tasks;
        console.log(usertasks);
        let temp=usertasks.map((task,index)=>{
            return <TaskCard color={tas} content={task.content} key={index} id={task.id}/>
        })
        setTasks(temp);
        console.log(tasks);
    },[store.currUser,curr]);
    
    function addTask(){
        let newObj = { ...curr };
        let newTask={
            content:taskRef.current.value,
            id:newObj.uniId+1,
            color:shinyColors[getRandomNumber()]
        }
        newObj.tasks.push(newTask);
        newObj.uniId++;
        console.log(newObj);
        setCurr(newObj);
        store.updateCurrUser(newObj);
        fetch("http://localhost:5000/addtask",{
            method: "POST",
            headers:{
                "content-type": "application/json",
            },
            body:JSON.stringify(newObj)
        }).then((response)=>{
            if (!response.ok) {
                throw new Error('Network response was not ok');
              }
            else{
                
                return response.json();
            }
        }).then((data)=>{
            setCurr(data);
            store.updateCurrUser(data);
        })
    }

    return ( 
    <div className="tasks">
        <div>
            <input ref={taskRef} type="text" />
            <button onClick={addTask}>Add</button>
        </div>
            {tasks}
    </div> 
    );
}
 
export default Tasks;
