import { useContext } from "react";
import { Store } from "../Store";

const OnGoingCard = (props) => {
    let store=useContext(Store);
    function removeOngoing(){
        fetch(`http://localhost:5000/removeOngoing/${props.id}/${store.currUser.username}`).then((response)=>{
            if(!response.ok){
                throw new Error(response);
            }
            else{
                return response.json();
            }
        }).then((data)=>{
            console.log(data);
            store.updateCurrUser(data);
        })
    }
    return ( 
        <div style={{backgroundColor:props.color}} className="taskcard">
            <h2>{props.content}</h2>
            <div>
                <button className="start"><i class="fa-solid fa-check fa-xl"></i></button>
                <button onClick={removeTask} className="start"><i className="fa-solid fa-ban fa-xl"></i></button>
            </div>
            <p>{props.time}</p>
        </div>
     );
}
 
export default OnGoingCard;
